import React from 'react';
import MusicList from "./content/MusicList.json"
import {Redirect} from "react-router-dom"
import "./pages.css"

const style = {
    border: '0',
    height: '100%'
};

const BandCamp = [
'https://bandcamp.com/EmbeddedPlayer/track=101063964/size=small/bgcol=ffffff/linkcol=000000/tracklist=false/transparent=true/',
'https://bandcamp.com/EmbeddedPlayer/track=1471844362/size=small/bgcol=ffffff/linkcol=0687f5/tracklist=false/transparent=true/',
'https://bandcamp.com/EmbeddedPlayer/track=2366630471/size=small/bgcol=ffffff/linkcol=0687f5/tracklist=false/transparent=true/'
];

const MusicPage = (props) => {
  const ValidId = parseInt(props.match.params.id)
  if(!ValidId){
    return <Redirect to="/404" />
  }
  const fetchedSong = {}
  MusicList.forEach((item, i) => {
    if(ValidId === item.id){
      fetchedSong.title = item.title ? item.title: "No title given"
      fetchedSong.lyrics = item.lyrics ? item.lyrics: "No lyrics available"
      fetchedSong.video = item.video ? item.video: "No video given"
      fetchedSong.bandcamp = item.bandcamp ? item.bandcamp: "No content given"
    }
  });

  return(
    <div key={fetchedSong.id} className="song">
          <h1>{fetchedSong.title}</h1>
          <iframe style={style} title={fetchedSong.title}
          src={`https://bandcamp.com/EmbeddedPlayer/track=${fetchedSong.bandcamp}/size=small/bgcol=ffffff/linkcol=000000/tracklist=false/transparent=true/`} seamless>
              <a href={`https://pedrobergamo.bandcamp.com/track/${fetchedSong.title}`}>{fetchedSong.title}</a>
          </iframe>
          {fetchedSong.lyrics.map((verse, i) => {
            return <p key={i} className="lyrics">{verse}</p>
          })}
          {console.log(fetchedSong.bandcamp)}

    </div>
    );
};

export default MusicPage;
