import React from 'react';
import { slide as Slide } from 'react-burger-menu';
import {
     Link
  } from 'react-router-dom';

import menu from "../images/Icons/menu.png"
var Shop = "https://www.etsy.com/fi-en/shop/Visibilium"

export default class Menu extends React.Component {
  constructor (props) {
      super(props)
      this.state = {
        menuOpen: false
      }
    }

    // This keeps your state in sync with the opening/closing of the menu
    // via the default means, e.g. clicking the X, pressing the ESC key etc.
    handleStateChange (state) {
      this.setState({menuOpen: state.isOpen})
    }

    // This can be used to close the menu, e.g. when a user clicks a menu item
    closeMenu () {
      this.setState({menuOpen: false})
    }

    // This can be used to toggle the menu, e.g. when using a custom icon
    // Tip: You probably want to hide either/both default icons if using a custom icon
    // See https://github.com/negomi/react-burger-menu#custom-icons
    toggleMenu () {
      this.setState(state => ({menuOpen: !state.menuOpen}))
    }

    render() {
        return (
          <div >
              <Slide
                right
                customBurgerIcon={ <img src={menu} alt="menu png"/>}
                width={ '100%' }
                isOpen={this.state.menuOpen}
                onStateChange={(state) => this.handleStateChange(state)}
              >
              <nav className="menu">
                  <li onClick={() => this.closeMenu()} className="menu_link"><Link to=
                  "/design">Design</Link></li>
                   <li onClick={() => this.closeMenu()} className="menu_link"><Link to=
                    "/music">Music</Link></li>
                    <a onClick={() => this.closeMenu()} className="menu_link" href={Shop} target="_blank" rel="noreferrer">Shop</a>
              </nav>
              </Slide>
              </div>

        )
      }}
